import { useRouter } from 'next/router';

const Produto = () => {
  const router = useRouter();
  const { item } = router.query;

  // Mapeie os produtos para URLs de imagens da internet
  const imagensDosProdutos = {
    camisa: 'data:image/jpeg;base64,',
    calca: 'data:image/jpeg;base64,',
    sapato: 'data:image/jpeg;base64,',
    bone: 'data:image/jpeg;base64,',
    oculos: 'data:image/jpeg;base64,',
  };

  const imagemDoProduto = imagensDosProdutos[item] || 'data:image/jpeg;base64,';

  return (
    <div>
      <h1>Produto: {item}</h1>
      <image src={imagemDoProduto} alt={item} />
    </div>
  );
};

export default Produto;
